import React from 'react'
import { bindActionCreators } from "redux";
import classes from './User.module.css'
import { connect } from "react-redux";
import { requestApiUser } from "../../../actions";


class User extends React.Component {

  componentDidMount() {
    this.props.requestApiUser(this.props.userId);
  }

  render() {
    console.log('NEW USER:', this.props.users)
    return (
      <div className={classes.User}>{this.props.userId}
      </div>
    )
  }
}
const mapStateToProps = state => ({users: state.users});
const mapDispatchToProps = (dispatch) => {
  return {
    requestApiUser: (id)=>{dispatch(requestApiUser(id))},
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(User)
