import { RECEIVE_API_DATA, RECEIVE_API_POST, RECEIVE_API_USER  } from "../actions";

iState = {
  postr: '1'
}

export default (state = iState, { type, data }) => {
  switch (type) {
    case RECEIVE_API_DATA:
      return {
          posts: data
      }
    case RECEIVE_API_POST:
      return {
          post: [data]
      }
    case RECEIVE_API_USER:
      return {
          userData: [data]
      }
    default:
      return state;
  }
};

console.log('state', iState)
