import React from 'react'
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { requestApiData } from "../../actions";
import Post from './Post/Post'


class Posts extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      searchTerm: '',
      currentDispayed: this.props.data.posts
    }

    this.onInputChange = this.onInputChange.bild(this)

  }

  componentDidMount() {
    this.props.requestApiData();
  }

  // onInputChange(event) {
  //   let new = _.filter(this.props.data.posts)
  // }


  posts = (key, inder) => (

      <Post key={inder} userId={key.userId} postId={key.id} title={key.title} body={key.body} />

  )

  render() {
    let filteredCotacts = this.posts
    return (

      <div className="Posts">
        <div className={"Search"}>
          <input type="text" onChange={this.text} />
        </div>
          {(this.props.data.posts || []).map(filteredCotacts)}
      </div>

    )
  }
}

const mapStateToProps = state => ({data: state.data});
const mapDispatchToProps = dispatch => bindActionCreators({requestApiData}, dispatch)

export default connect(mapStateToProps, mapDispatchToProps)(Posts)
