import React from 'react';
import classes from './Post.module.css'
import {withRouter} from 'react-router-dom'

const Post = props => {
  const text = props.body
  return (
    <div onClick={()=>props.history.push('/posts/'+props.postId)} className={classes.Post}>

      <div className={classes.PostContent}>
        <h2>{props.title}</h2>
        <p>{text}</p>
        <a href="javascript:void(0);">If you like click buttons - use it :)</a>
      </div>
    </div>
  )
}

export default withRouter(Post)
