      if(key.body.index('quia et suscipit suscipit') > -1) {
        seacrhResult = {
          key: key
        }
      }
    });
  }
    console.log(event.target.value, this.props.data.posts)
  }

  posts = (key, inder) => (

      <Post key={inder} userId={key.userId} postId={key.id} title={key.title} body={key.body} />

  )

  render() {
    return (

      <div className="Posts">
        <div className={"Search"}>
          <input type="text" onChange={this.text} />
        </div>
          {(this.props.data.posts || []).map(this.posts)}
      </div>

    )
  }
}

const mapStateToProps = state => ({data: state.data});
const mapDispatchToProps = dispatch => bindActionCreators({requestApiData}, dispatch)

export default connect(mapStateToProps, mapDispatchToProps)(Posts)
