import React from 'react'
import { bindActionCreators } from "redux";
import classes from './../Post.module.css'
import User from '../../../Users/User/User'
import { connect } from "react-redux";
import { requestApiPost } from "../../../../actions";


class PostSingle extends React.Component {

  componentDidMount() {
    //this.props.requestApiUser(1);
    this.props.requestApiPost(this.props.match.params.postId);
  }


  // componentWillReceiveProps(nextProps){
  //   const userId = nextProps.data.post.map((obj, index) => (obj.userId))[0]
  //   this.props.requestApiUser(userId)
  //   console.log(userId)
  //   return false
  // }

  render() {

    console.log('DATA: ',this.props.data)
    //const backgroundImg = 'http://www.fondsecran.eu/a/get_photo/64902/1920/1200'
    const backgroundImg = ''

      {(this.props.data.post || []).map((key,index)=>{
        return (
      <div className={classes.PostArea}>
        <div className={classes.Post}>
          <div className={classes.PostImage} style={{backgroundImage: 'url('+backgroundImg+')'}}></div>

                <div className={classes.PostContent}>
                  <div>{key.userId}</div>
                  <h2>{key.title}</h2>
                  <p>{key.body}</p>
              </div>

        </div>
        <User key={"user"} />
      </div>
        )})}

  }
}
const mapStateToProps = state => ({data: state.data});
//const mapDispatchToProps = dispatch => bindActionCreators({requestApiPost}, dispatch)
const mapDispatchToProps = (dispatch) => {
  return {
    requestApiPost: (id)=>{dispatch(requestApiPost(id))},
    //requestApiUser: (id)=>{dispatch(requestApiUser(id))},
  }
}

//
// {(this.props.data.user || []).map((key,index)=>{
//   return (
// <div className={classes.User}>
//   {key.name}
// </div>
// )})}


export default connect(mapStateToProps, mapDispatchToProps)(PostSingle)
