// ALL POST REQUEST________________________________________________________
export const REQUEST_API_DATA = "REQUEST_API_DATA";
export const RECEIVE_API_DATA = "RECEIVE_API_DATA";

export const requestApiData = () => ({ type: REQUEST_API_DATA });
export const receiveApiData = data => ({ type: RECEIVE_API_DATA, data });
//_________________________________________________________________________



// POST SINGLE REQUEST_____________________________________________________
export const REQUEST_API_POST = "REQUEST_API_POST";
export const RECEIVE_API_POST = "RECEIVE_API_POST";

export const requestApiPost = (id,userId) => {
  return { type: REQUEST_API_POST, id,userId }
}
console.log(userId)
export const receiveApiPost = (data) => ({ type: RECEIVE_API_POST, data });
//_________________________________________________________________________



// USER REQUEST____________________________________________________________
export const REQUEST_API_USER = "REQUEST_API_USER";
export const RECEIVE_API_USER = "RECEIVE_API_USER";

export const requestApiUser = (id) => {
  return { type: REQUEST_API_USER, id }
}
export const receiveApiUser = (data) => ({ type: RECEIVE_API_USER, data });
//_________________________________________________________________________
