import { RECEIVE_API_DATA, RECEIVE_API_POST, RECEIVE_API_USER  } from "../actions";


export default (state = {}, { type, data }) => {
  switch (type) {
    case RECEIVE_API_DATA:
      return {
          posts: data
      }
    case RECEIVE_API_POST:
      return {
          post: [data],
          user: [data]
      }
    case RECEIVE_API_USER:
      return {
          user: [data]
      }
    default:
      return state;
  }
};
