import React from 'react'
import { bindActionCreators } from "redux";
import classes from './../Post.module.css'
import { connect } from "react-redux";
import { requestApiPost } from "../../../../actions";

class PostSingle extends React.Component {
  componentDidMount() {
    // Call Action Creator
    this.props.requestApiPost();
    console.log(this.props.post)
  }
  render() {
    person = (x, i) => (
      <h1>{x.title}</h1>
    )

    const backgroundImg = 'http://www.fondsecran.eu/a/get_photo/64902/1920/1200'
    return (
      <div className={classes.Post}>
        <div className={classes.PostImage} style={{backgroundImage: 'url('+backgroundImg+')'}}></div>
        <div className={classes.PostContent}>
          {(this.props.data.post || []).map(this.currentPost)}
          <h2>{this.props.title}</h2>
          <p>{this.props.text}</p>
          <div className={classes.PostNotRealButton}>If you like click buttons - use it :)</div>
        </div>
      </div>
    )
  }
}
const mapStateToProps = state => ({data: state.data});
const mapDispatchToProps = dispatch => bindActionCreators({requestApiPost}, dispatch)

export default connect(mapStateToProps, mapDispatchToProps)(PostSingle)
