import React from 'react'
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { requestApiData } from "../../actions";
import Post from './Post/Post'


class Posts extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      search: 'Level Upo'
    }
  }

  componentDidMount() {
    this.props.requestApiData();
  }

  updateSearch(event) {
    this.setState({
      search: event.target.value
    })
  }


  posts = (key, inder) => (

      <Post key={inder} userId={key.userId} postId={key.id} title={key.title} body={key.body} />

  )

  render() {
    let filteredPosts = (this.props.data.posts || []).filter(
      (post) => {
        return post.title.indexOf(this.state.seacrh) != -1;
      }
    );
    return (

      <div className="Posts">
        <div className={"Search"}>
          <input type="text"  onChange={this.updateSearch} value={this.state.seacrh} />
        </div>
          {(this.props.data.posts || []).map(filteredPosts)}
      </div>

    )
  }
}

const mapStateToProps = state => ({data: state.data});
const mapDispatchToProps = dispatch => bindActionCreators({requestApiData}, dispatch)

export default connect(mapStateToProps, mapDispatchToProps)(Posts)
