import { call, all, take, put, takeEvery, takeLatest } from "redux-saga/effects";
import { REQUEST_API_DATA, receiveApiData, REQUEST_API_POST, receiveApiPost, REQUEST_API_USER, receiveApiUser } from "./actions";
import { fetchData, fetchPost, fetchUser } from "./api";


function* getApiData(action) {
  try {
    const data = yield call(fetchData);
    yield put(receiveApiData(data));
  } catch (e) {
    console.log('Error',e);
  }
}

function* getApiPost(action) {
  try {
    const data = yield call(fetchPost, action.id);
    yield put(receiveApiPost(data));
  } catch (e) {
    console.log('Error',e);
  }
}

function* getApiUser(action) {
  try {
    const data = yield call(fetchUser, action.id);
    yield put(receiveApiUser(data));
    console.log('FETCH: ',data)
  } catch (e) {
    console.log('Error',e);
  }
}


export default function* mySaga() {
  yield* [
    take(REQUEST_API_DATA, getApiData),
    take(REQUEST_API_USER, getApiUser),
    take(REQUEST_API_POST, getApiPost),
  ]
}
