import React from 'react'
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { requestApiData } from "../../actions";
import Post from './Post/Post'


class Posts extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      search: '',
      currentData: [{body: "quia et suscipit↵suscipit recusandae consequuntur expedita et cum↵reprehenderit molestiae ut ut quas totam↵nostrum rerum est autem sunt rem eveniet architecto",
id: 1,
title: "sunt aut facere repellat provident occaecati excepturi optio reprehenderit",
userId: 1}]
    }
    console.log('THISIS: ',this.state.currentData)
  }

  componentDidMount() {
    this.props.requestApiData();
  }


  thisTest = (event) => {
    let searchResult = this.props.data.posts;
    const searchQuery = event.target.value
    if(searchResult !== undefined) {
      searchResult = searchResult.filter(function(i){
        if(i.body.search(searchQuery) || i.title.search(searchQuery)) {
          console.log("inside ",i.body.search('$'))
          return
        }
      })
    }
    this.setState({
      currentData: searchResult
    })
    console.log("searchResult", searchResult)
  }


  posts = (key, inder) => (

      <Post key={inder} userId={key.userId} postId={key.id} title={key.title} body={key.body} />

  )

  render() {
    return (

      <div className="Posts">
        <div className={"Search"}>
          <input type="text" onChange={this.thisTest} />
        </div>
          { (this.state.currentData === undefined) ? (this.props.data.posts || []).map(this.posts) : (this.state.currentData || []).map((x,i)=>{ return( <div>{x.body}</div> ) })}
      </div>

    )
  }
}

const mapStateToProps = state => ({data: state.data});
const mapDispatchToProps = dispatch => bindActionCreators({requestApiData}, dispatch)

export default connect(mapStateToProps, mapDispatchToProps)(Posts)
