import React from 'react'
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { requestApiData } from "../../actions";
import Post from './Post/Post'

//const Posts = props => {
class Posts extends React.Component {
  //
  // state = {
  //   cars: [
  //     {name: 'Ford', year: 2018},
  //     {name: 'Audi', year: 2016},
  //     {name: 'Mazda', year: 2010}
  //   ]
  // }

  componentDidMount() {
    // Call Action Creator
    this.props.requestApiPostsData();

  }

  person = (x, i) => (
    <Post key={i} userId={x.userId} postId={x.id} title={x.title} body={x.body} />
  )

  render() {
    return (
      <div className="Posts">

          {(this.props.data.posts || []).map(this.person)}

      </div>
    )
  }
}

const mapStateToProps = state => ({data: state.data});
const mapDispatchToProps = dispatch => bindActionCreators({requestApiData}, dispatch)

export default connect(mapStateToProps, mapDispatchToProps)(Posts)
