import React from 'react'
import classes from '../../Post.module.css'

export default class PostSingle extends React.Component {
  render() {
    const backgroundImg = 'http://www.fondsecran.eu/a/get_photo/64902/1920/1200'
    return (
      <div className={classes.Post}>
        <div className={classes.PostImage} style={{backgroundImage: 'url('+backgroundImg+')'}}></div>
        <div className={classes.PostContent}>
          <h2>{props.title}</h2>
          <p>{props.text}</p>
          <div className={classes.PostNotRealButton}>If you like click buttons - use it :)</div>
        </div>
      </div>
    )
  }
}
